YUI.add("gallery-xarno-proximity",function(a){a.namespace("Xarno").Proximity=a.Base.create("gallery-xarno-proximity",a.Plugin.Base,[],{locate:function(){this._getDistance();return this.get("distance");},_getDistance:function(){var h=this.get("target").get("region"),g=this.get("host").get("region"),e="top",f="right",c="bottom",d="left",j=[Math.abs(Math.sqrt(Math.pow(g[e]-h[e],2)+Math.pow(g[f]-h[f],2))),Math.abs(Math.sqrt(Math.pow(g[f]-h[f],2)+Math.pow(g[c]-h[c],2))),Math.abs(Math.sqrt(Math.pow(g[c]-h[c],2)+Math.pow(g[d]-h[d],2))),Math.abs(Math.sqrt(Math.pow(g[d]-h[d],2)+Math.pow(g[e]-h[e],2)))],i=Math.min(j[0],j[1],j[2],j[3]);this.set("distance",i);},_targetSetter:function(b){return a.Node(b);}},{NS:"proximity",ATTRS:{target:{},distance:{}}});},"@VERSION@",{requires:["base-build","event","plugin"]});